import React, { Component } from "react";
import {
    Card,
    CardImg,
    CardBody,
    CardSubtitle,
    Button,
    Modal,
    Col,
    Input,
    ModalHeader,
    ModalBody,
    Row,
    Label,
    FormFeedback
} from "reactstrap";
import { Link } from "react-router-dom";
import { Control, LocalForm, Errors } from "react-redux-form";
import { FadeTransform } from "react-animation-components";

const required = (val) => val && val.length;
const maxLength = (len) => (val) => !val || val.length <= len;
const minLength = (len) => (val) => val && val.length >= len;
const isNumber = (val) => !isNaN(Number(val));

// Presentational component (const) dùng để Render danh sách từng nhân viên
const RenderStaffItem = ({ staff, onDeleteStaff }) => {
    return (
        <FadeTransform
            in
            transformProps={{
                exitTransform: "scale(0.5) translateY(-50%)"
            }}
        >
            <div>
                <Link to={`/staff/${staff.id}`}>
                    <Card>
                        <CardImg width="100%" src={staff.image} alt={staff.name} />
                        <CardBody>
                            <CardSubtitle>{staff.name}</CardSubtitle>
                        </CardBody>
                    </Card>
                </Link>
                <Button color="danger" onClick={() => onDeleteStaff(staff.id)}>
                    Delete
                </Button>
            </div>
        </FadeTransform>
    );
};

// Presentational component (const)
class StaffList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nameF: ""
        };
        /* Ràng buộc 2 chiều đối với các hàm được khai báo bên dưới */
        this.timNhanvien = this.timNhanvien.bind(this);
    }

    /* Hàm tìm kiếm từ khóa tên nhân viên và render ra kết quả tìm kiếm nhân viên  */
    timNhanvien(event) {
        const nameS = event.target.nameS.value;
        event.preventDefault();
        this.setState({ nameF: nameS });
    }

    render() {
        const staffList = this.props.staffs
            .filter((val) => {
                if (this.state.nameF === "") return val;
                else if (
                    val.name.toLowerCase().includes(this.state.nameF.toLowerCase())
                )
                    return val;
                return 0;
            })
            .map((val) => {
                return (
                    <div className="col-6 col-md-4 col-lg-2 mt-3 mb-3" key={val.id}>
                        <RenderStaffItem
                            staff={val}
                            onDeleteStaff={this.props.onDeleteStaff}
                        />
                    </div>
                );
            });

        //Render giao diện Staff list
        return (
            <div className="container">
                <div className="row">
                    <div className="col-12 col-md-6 mt-3">
                        <div className="row">
                            <div className="col-10 col-md-10">
                                <h3>Nhân viên</h3>
                            </div>
                            <AddStaffForm onAdd={this.props.onAddStaff} />
                        </div>
                    </div>
                    <div className="col-12 col-md-6 mt-3">
                        <form onSubmit={this.timNhanvien} className="form-group row">
                            <div className="col-8 col-md-8">
                                <input
                                    type="text"
                                    name="nameS"
                                    className="form-control"
                                    placeholder="Tìm kiếm nhân viên ..."
                                />
                            </div>
                            <div className="col-4 col-md-4">
                                <button className="btn btn-primary" type="submit">
                                    Tìm kiếm
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
                <div className="col-12">
                    <hr />
                </div>

                <div className="row shadow mb-5 mt-5">{staffList}</div>
            </div>
        );
    }
}

class AddStaffForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modalOpen: false,
            doB: "",
            startDate: "",
            departmentId: "Dept02",
            image: "/assets/images/alberto.png",
            touched: {
                doB: false,
                startDate: false
            }
        };

        /* Ràng buộc 2 chiều đối với các hàm được khai báo bên dưới */
        this.toggleModal = this.toggleModal.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleBlur = this.handleBlur.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    /* Hàm xử lý trả touched về true sau khi người dùng không nhập gì mà thoát khỏi input text */
    handleBlur = (field) => (evt) => {
        this.setState({
            touched: { ...this.state.touched, [field]: true }
        });
    };

    /* Hàm xử lý việc nhập liệu vào ô input, thay đổi trạng thái dữ liệu */
    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }
    /* Hàm xử lý khi submit dữ liệu vào biểu mẫu */
    handleSubmit = (value) => {
        if (!this.state.doB || !this.state.startDate)
            this.setState({
                touched: { doB: true, startDate: true }
            });
        else {
            const newStaff = {
                name: value.name,
                doB: this.state.doB,
                salaryScale: parseInt(value.salaryScale, 10),
                startDate: this.state.startDate,
                image: "/assets/images/alberto.png",
                departmentId: this.state.departmentId,
                annualLeave: parseInt(value.annualLeave, 10),
                overTime: parseInt(value.overTime, 10)
            };

            this.props.onAdd(newStaff);
        }
    };

    /* Hàm kiểm tra ngày tháng có được nhập hay không */
    validate(doB, startDate) {
        const errors = {
            doB: "",
            startDate: ""
        };

        if (this.state.touched.doB && doB.length < 1) errors.doB = "Yêu cầu nhập";
        if (this.state.touched.startDate && startDate.length < 1)
            errors.startDate = "Yêu cầu nhập";

        return errors;
    }

    /* Hàm Bật tắt Modal */
    toggleModal(e) {
        e.preventDefault();
        this.setState({
            modalOpen: !this.state.modalOpen
        });
    }

    render() {
        const errors = this.validate(this.state.doB, this.state.startDate); // Tạo biến báo lỗi khi người dùng khai báo thiếu
        return (
            <React.Fragment>
                <div className="col-2 col-auto">
                    <Button outline onClick={this.toggleModal}>
                        <span className="fa fa-plus fa-lg"></span>
                    </Button>
                </div>
                <Modal isOpen={this.state.modalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Thêm nhân viên</ModalHeader>
                    <ModalBody>
                        <LocalForm onSubmit={(value) => this.handleSubmit(value)}>
                            <Row className="control-group">
                                <Label htmlFor=".name" md={4}>
                                    Tên
                                </Label>
                                <Col md={8}>
                                    <Control.text
                                        model=".name"
                                        className="form-control"
                                        id=".name"
                                        name="name"
                                        validators={{
                                            required,
                                            minLength: minLength(3),
                                            maxLength: maxLength(30)
                                        }}
                                    />
                                    <Errors
                                        model=".name"
                                        className="text-danger"
                                        show="touched"
                                        messages={{
                                            required: "Yêu cầu  ",
                                            minLength: "nhập nhiều hơn 3 ký tự",
                                            maxLength: "Yêu cầu nhập ít hơn 30 ký tự"
                                        }}
                                    />
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Label htmlFor="doB" md={4}>
                                    Ngày sinh
                                </Label>
                                <Col md={8}>
                                    <Input
                                        type="date"
                                        name="doB"
                                        id="doB"
                                        valid={errors.doB === ""}
                                        invalid={errors.doB !== ""}
                                        onBlur={this.handleBlur("doB")}
                                        onChange={this.handleInputChange}
                                    />
                                    <FormFeedback>{errors.doB}</FormFeedback>
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Label htmlFor="startDate" md={4}>
                                    Ngày vào công ty
                                </Label>
                                <Col md={8}>
                                    <Input
                                        type="date"
                                        name="startDate"
                                        id="startDate"
                                        valid={errors.startDate === ""}
                                        invalid={errors.startDate !== ""}
                                        onBlur={this.handleBlur("startDate")}
                                        onChange={this.handleInputChange}
                                    />
                                    <FormFeedback>{errors.startDate}</FormFeedback>
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Label htmlFor="department" md={4}>
                                    Phòng ban
                                </Label>
                                <Col md={8}>
                                    <select
                                        name="departmentId"
                                        id="department"
                                        className="form-control"
                                        value={this.state.departmentId}
                                        onChange={(e) =>
                                            this.setState({ departmentId: e.target.value })
                                        }
                                    >
                                        <option value="Dept01">Sale</option>
                                        <option value="Dept02">HR</option>
                                        <option value="Dept03">Marketing</option>
                                        <option value="Dept04">IT</option>
                                        <option value="Dept05">Finance</option>
                                    </select>
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Label htmlFor="salaryScale" md={4}>
                                    Hệ số lương
                                </Label>
                                <Col md={8}>
                                    <Control.text
                                        model=".salaryScale"
                                        id="salaryScale"
                                        name="salaryScale"
                                        placeholder="1.0 -> 3.0"
                                        validators={{
                                            required,
                                            isNumber
                                        }}
                                        defaultValue="1"
                                        className="form-control"
                                    />
                                    <Errors
                                        model=".salaryScale"
                                        className="text-danger"
                                        show="touched"
                                        messages={{
                                            required: "Yêu cầu nhập",
                                            isNumber: "Phải là chữ số"
                                        }}
                                    />
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Label htmlFor="annualLeave" md={4}>
                                    Số ngày nghỉ còn lại
                                </Label>
                                <Col md={8}>
                                    <Control.text
                                        model=".annualLeave"
                                        id="annualLeave"
                                        name="annualLeave"
                                        defaultValue="0"
                                        validators={{
                                            required,
                                            isNumber
                                        }}
                                        className="form-control"
                                    />
                                    <Errors
                                        model=".annualLeave"
                                        className="text-danger"
                                        show="touched"
                                        messages={{
                                            required: "Yêu cầu nhập",
                                            isNumber: "Phải là chữ số"
                                        }}
                                    />
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Label htmlFor="overTime" md={4}>
                                    Số ngày đã làm thêm
                                </Label>
                                <Col md={8}>
                                    <Control.text
                                        model=".overTime"
                                        id="overTime"
                                        name="overTime"
                                        defaultValue="0"
                                        validators={{
                                            required,
                                            isNumber
                                        }}
                                        className="form-control"
                                    />
                                    <Errors
                                        model=".overTime"
                                        className="text-danger"
                                        show="touched"
                                        messages={{
                                            required: "Yêu cầu nhập",
                                            isNumber: "Phải là chữ số"
                                        }}
                                    />
                                </Col>
                            </Row>
                            <Row className="control-group">
                                <Col md={{ size: 10, offset: 2 }}>
                                    <Button type="submit" color="success">
                                        Thêm
                                    </Button>
                                </Col>
                            </Row>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </React.Fragment>
        );
    }
}

export default StaffList;
